CREATE TABLE CITIES (
  id INT AUTO_INCREMENT  PRIMARY KEY NOT NULL,
  name VARCHAR(250) NOT NULL,
  advice VARCHAR(2000) NOT NULL
);

INSERT INTO CITIES
    (name, advice)
        VALUES ('МИНСК', 'Прогуляйтесь по Троицкому предместью, а в национальной библиотеке нечего делать.'),
            ('ЛЮКСЕМБУРГ', 'Город замков, мостов и стен, что же тут посмотреть? Замок Берг, Казематы, Дворец Великих Герцогов и визитная карточка города – мост Адольфа'),
            ('СТОКГОЛЬМ', 'Серце Стокгольма – это конечно же Гамла Стан. Дальше идем к Королевскому дворцу и не забываем про дворец Дроттинингхольм. В самом сердце старого города находится площадь Стурторье. Любителям церквей тоже найдется на что посмотреть: Церковь Святой Клары, Святого Николая, церковь Риддархольмена.'),
            ('АНТВЕРПЕН', 'Небольшой город, но и тут есть на что посмотреть: Антверпенская ратуша, замок стен. Вас точно удивит здание администрации порта Антверпена конечно же вокзал – визитная карточка города!'),
            ('БРЮССЕЛЬ', 'Визитная карточка – писающий мальчик, но не забываем про писающую девочку и собачку. Лучший вариант для начала – это площадь Гран-Плас и Ратуша. Недалеко сходите в Парк 50-летия, Триумфальная арка есть не только в париже. Некогда резиденция монархов - Королевский дворец. Не пропустите Базилику Святого Сердца и Церковь Богоматери Побед в Саблоне. Соскучились по холмам? Тогда вам на Гору искусств.'),
            ('КАРЛОВЫ ВАРЫ', 'По приезду сразу идите к источникам термальной воды, не забудьте с собой кружку. Пока наслаждаетесь вкусом воды, прогуляйтесь по Мельничной коллонаде, Рыночной коллонаде, Гейзерной коллонаде, Замковой коллонаде, Содовой коллонаде.'),
            ('ВЕНЕЦИЯ', 'Венеция – город каналов, так что начнем с Гранд канала. “Маст визит ” Венеции – Площадь Святого Марка, где не пропустите Собор Святого Марка и Колокольную и Часовую башни. Вечером сходите полюбоваться дворцом Дождей. Проплывая на гондоле по каналам посмотрите на Собор Санта-Мария-делла-Салюте, остров Сан-Джорджо-Маджоре и конечно же Мост Риальто.'),
            ('ПРАГА', 'Собор Святого Вита обязателен к посещению. Вацлавская площадь прекрасна, но деньги там менять не стоит.'),
            ('МИЛАН', 'В первую очередь Миланский собор и Площадь дуомо – визитные карточки города. Отсюда пройдитесь по Галерее Виктора Эммануила II к театру Ла Скала. Самый знаменитый замок – замок Сфорца. Особо любознательные, отправляйтесь в музей науки и технологий Леонардо Да Винчи. А вечером прокатитесь по каналу Гранд Новильо'),
            ('РИМ', 'Первым делом отправляйтесь к символу Рима – Колизею. Окунитесь в эпоху Древнего Рима любуясь Римским форумом на холме Палантин. Любите лестницы? Тогда Площадь Испании для вас. Любителям фонтанов просто необходимо увидеть Фонтан Треви, если вы сможете пробиться к нему сквозь толпу турисовм. По пути к нему отпустите себе грехи в древнем храме – Пантеоне.'),
            ('ЖЕНЕВА', 'Идём к Женевскому водохранилищу, где можно полюбоваться на фонтан Же д’О. Пару минут – и вот вы рядом с Собором Святого Петра. Как же без цветочков – Ботанический сад Женевы вам в помощь. Любителям необычной архитектуры понравится квартал Les Grottes.'),
            ('ВЕНА', 'Не забудьте попробовать знаменитый венский шницель, силы вам понадобятся. Символ Вены – Сабор Святого Стефана. Ратушная площадь, особенно вечером при иллюминации.'),
            ('ФЛОРЕНЦИЯ', 'Сразу идите в центр любоваться Сабором святой Марии, чей купол видел каждый на открытках и фото. Понте Веккьо – самый узнаваемый мост Флоренции. Любите искусство – Галерея Уфицци, Базилика Сан-Лоренцо для вас. За лучшей понорамой бегом на площадь Микельанджело – лучшие закаты под бокал вина именно здесь. Если времени совсем мало – Исторический центр Флоренции. И конечно же знаменитое Итальянское джелатто поднимет настроение на весь день!'),
            ('БАРСЕЛОНА', 'Первая ассоциация при слове Барселона – Саграда Фамилия, одно из творений Гауди – туда и отправляйтесь в первую очередь. Обязательным к посещению будет парк Гуэля – место, где действительно есть на что посмотреть. Все лучшие музеи Барселоны собраны на Холме Монжуик. Бонусом получите красивый вид на город. Но лучшее место, чтобы полюбоваться панорамой города – Холм Тибидабо. Если хотите потратить деньги, отправляйтесь на Рамблу – самая известная улица города со всевозможными сувенирными лавками, магазинчиками и вкусностями.');